using Aspose.Pdf;
using ChinaBM.Common;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Web;
using System.Web.UI;
using System.Web.UI.WebControls;

namespace Tools
{
    public partial class index : System.Web.UI.Page
    {
        protected void Page_Load(object sender, EventArgs e)
        {
            if (HttpKit.IsPost && Request.Files.Count > 0)
            {
                var file = Request.Files[0];
                new Aspose.Pdf.License().SetLicense(new MemoryStream(Convert.FromBase64String("")));
                new Aspose.Cells.License().SetLicense(new MemoryStream(Convert.FromBase64String("")));
                new Aspose.Slides.License().SetLicense(new MemoryStream(Convert.FromBase64String("")));
                new Aspose.Words.License().SetLicense(new MemoryStream(Convert.FromBase64String("")));

                
                var document = new Document(file.InputStream);

                // 实例化DocSaveOptions对象
                DocSaveOptions saveOptions = new DocSaveOptions();
                //将输出格式指定为DOCX
                saveOptions.Format = DocSaveOptions.DocFormat.DocX;

                document.Save(Response, "aa.docx", ContentDisposition.Inline, saveOptions);
            }
        }
    }
}